{{Infobox NRHP | name = Site of the First Self Sustaining Nuclear Reaction | nrhp_type=nhl | designated_other1_name= Chicago Landmark | designated_other1_date= 27 October 1971<ref name=CL/> | designated_other1_abbr= CL | designated_other1_link= Chicago Landmark | designated_other1_color= #aaccff | designated_nrhp_type = 18 February 1965<ref name="nhl"> {{cite web |title=Site of the First Self-Sustaining Nuclear Reaction |url=http://tps.cr.nps.gov/nhl/detail.cfm?ResourceId=204&ResourceType=Site |archiveurl=https://web.archive.org/web/20150405180502/http://tps.cr.nps.gov/nhl/detail.cfm?ResourceId=204&ResourceType=Site |archivedate=5 April 2015 |work=National Historic Landmark Summary Listing |publisher=[[National Park Service]] |accessdate=26 July 2013 }}</ref> | image = Stagg Field reactor.jpg | caption = Drawing of the reactor | location = [[Chicago]], Illinois, US | nearest_city = | lat_degrees = 41 | lat_minutes = 47 | lat_seconds =32 | lat_direction = N | long_degrees = 87 | long_minutes = 36 | long_seconds = 3 | long_direction = W | locmapin = United States Chicago Greater | area = | built = 1942<ref name="nhl"/> | architect = | architecture = | added = 15 October 1966 (66000314)<ref name=nris/> | visitation_num = | visitation_year = | refnum = 66000314<ref name="nris">{{NRISref|version=2010a|dateform=dmy}}</ref> | mpsub = | governing_body =[[Regenstein Library]] }} {{Infobox nuclear reactor | name = Chicago Pile-1 (CP-1) <!-- | image = Stagg Field reactor.jpg | image_caption= A drawing of the Chicago Pile-1 reactor. --> | location = Chicago, Illinois | concept = [[Research reactor]] | design_label= Designed and build by | design = [[Metallurgical Laboratory]] | operation = 1942 to 1943 | status = Dismantled | fuel = Natural uranium | fuel_state = Solid (pellets) | spectrum = [[Thermal-neutron reactor|Thermal]] | control = Control rods | coolant = None | moderator = [[Nuclear graphite]] (bricks) | use = Experimental | remarks = The Chicago Pile-1 (CP-1) is the w